import './App.css';
import { useState } from 'react';


function App() {
  var [inputValue, setInputValue] = useState('0.0');

  function test(){
    console.log("Successful!");
  }

  const handleInputChange = (event) => {
     setInputValue(event.target.value);
  }

  return (
    <div className="currencyExchange">

          <h2>Currency Exchange</h2>
          <div className="container1">
          <p>
          <input type="text" id="currency1" onChange={handleInputChange} value={inputValue}></input> 
          <select>
            <option value="usd">USD</option>
            <option value="myr">MYR</option>
          </select>
          <button type="button" id="exchangeButton" onClick={test}>&#x21CB;</button>
          <input type="text" id="currency2" ></input>
          <span id="result"></span>
          <select>
            <option value="usd">MYR</option>
            <option value="myr">USD</option>
          </select>

        </p>
        </div>
    </div>
  );

}






export default App;
