import './App.css';


function App() {
  function Calculate(){
    var value = 1000;
    var result = value * 1000;
    document.getElementById('result').innerHTML= result;
  }
  return (
    <div class="currencyExchange">

          <h2>Currency Exchange</h2>
          <div class="container1">
          <p>
          <input type="text" id="currency1" ></input> 
          <select>
            <option value="usd">USD</option>
            <option value="myr">MYR</option>
          </select>
          <button type="button" id="exchangeButton"  onclick={Calculate()}>&#x21CB;</button>
          <input type="text" id="currency2" ></input>
          <span id="result"></span>
          <select>
            <option value="usd">MYR</option>
            <option value="myr">USD</option>
          </select>

        </p>
        </div>
    </div>
  );

}




export default App;
