import './App.css';
import { useState } from 'react';


function App() {
  var [inputValue, setInputValue] = useState('0.0');

  function test(){
    console.log("Successful!");
  }

  function inputChangeValue(event) {
     var newValue = setInputValue(event.target.value);
     return newValue;
  }

  function currency2value(){
    var value = document.getElementById("currency1").value;
    return value;
  }



  return (
    <div className="currencyExchange">

          <h2>Currency Exchange</h2>
          <div className="container1">
          <p>
          <input type="text" id="currency1" onChange={inputChangeValue} value={inputValue}></input> 
          <select>
            <option value="usd">USD</option>
            <option value="myr">MYR</option>
          </select>
          <button type="button" id="exchangeButton" onClick={test}>&#x21CB;</button>
          <input type="text" id="currency2" onChange={currency2value} ></input>
          <span id="result"></span>
          <select>
            <option value="usd">MYR</option>
            <option value="myr">USD</option>
          </select>

        </p>
        </div>
    </div>
  );

}






export default App;
